<style lang="sass">
.p-article__sharearea
  margin: 48px 0% 0
  font-size: 18px
  color: #111111
  +font-bold
  +sp-view
    width: 195px
    height: auto
    margin: 32px auto 0
    text-align: center

.p-article__share-content
  display: flex

.p-article__share-item
  width: 144px
  height: 48px
  margin-right: 16px
  +sp-view
    width: 48px
    height: 48px
    margin: 12px 24px 0 0
  &:last-child
    margin-right: 0

.p-article__share-link
  position: relative
  display: flex
  justify-content: space-between
  align-items: center
  width: 144px
  height: 48px
  padding: 0 24px
  border-radius: 3px
  svg
    transition: .4s
  &:hover
    color: #ffffff
    svg
      fill: #ffffff

.share-insta
  .p-article__share-link
    border: solid 1px transparent
    border-image: url('/assets/img/share/border-insta.svg')
    &::after
      content: ""
      position: absolute
      top: 0
      left: 0
      background: linear-gradient(45deg, #EF4563, #EF6E45)
      border-radius: inherit
      margin: -1px
      transition: .4s
      z-index: -1
    &:hover
      &::after
        opacity: 0


.share-twitter
  .p-article__share-link
    border: 1px solid #55ACEE
    &:hover
      background-color: #55ACEE
    +sp-view
      &::before
        height: 20px

.share-face
  .p-article__share-link
    border: 1px solid #49629D
    &:hover
      background-color: #49629D

</style>
